  file "open" passed_data "onlyaccess"
  file "get" 4
  data.replace("/^[ \n\t]*/gm","")
  ots_script = data.split("\n")
  each i line ots_script (
    if line.startsWith("if ") or line.startsWith("jump ") (
      line.split(" ")
      if line.last().[1] == ":" (
        idx = ots_script.index(line.last())
        line.[line.len] = idx
        ots_script.[i] = line.join(" ").destr
      )
    )
  )
  mode = "ots"
) else (
  ots_script = ""
)

